const video = document.querySelector('.webcam');
const canvas = document.querySelector('.video');
const ctx = canvas.getContext('2d');
const faceCanvas = document.querySelector('.face');
const faceCtx = faceCanvas.getContext('2d');
const faceDetector = new window.FaceDetector();
const SIZE = 10;
const SCALE = 1.35; // write a func that will populate the users video

async function populateVideo() {
  const stream = await navigator.mediaDevices.getUserMedia({
    video: {
      width: 400,
      height: 250
    }
  });
  video.srcObject = stream; // await video.play();
  //   size canvas to be same as video

  console.log(video.videoWidth, video.videoHeight);
  canvas.width = video.videoWidth;
  canvas.height = video.videoHeight;
  faceCanvas.width = video.videoWidth;
  faceCanvas.height = video.videoHeight;
}

function drawFace(face) {
  const {
    width,
    height,
    top,
    left
  } = face.boundingBox;
  ctx.clearRect(0, 0, canvas.width, canvas.height);
  ctx.strokeStyle = '#ffc600';
  ctx.lineWidth = 2;
  ctx.strokeRect(left, top, width, height);
}

function censor({
  boundingBox: face
}) {
  faceCtx.imageSmoothingEnabled = false;
  faceCtx.clearRect(0, 0, faceCanvas.width, faceCanvas.height); // draw small face

  faceCtx.drawImage(video, // where does the video come from
  face.x, // where do we start the source pull from?
  face.y, face.width, face.height, // 4 draw args
  face.x, // where should we start drawing the x and y?
  face.y, SIZE, SIZE); // take that face back out and draw it back at normal size.

  const width = face.width * SCALE;
  const height = face.height * SCALE;
  faceCtx.drawImage(faceCanvas, // source
  face.x, // where should we start the pull from?
  face.y, SIZE, SIZE, // drwing args
  face.x - (width - face.width) / 2, face.y - (height - face.height) / 2, width, height);
}

async function detect() {
  const faces = await faceDetector.detect(video);
  console.log(faces.length); // ask the browser when the next animation frame is and run detect for us

  faces.forEach(drawFace);
  faces.forEach(censor);
  requestAnimationFrame(detect);
}

populateVideo().then(detect); // console.log('it works');
